// frontend/src/App.js
import React, { useState } from 'react';
import axios from 'axios';

function App() {
  const [input, setInput] = useState('');
  const [draft, setDraft] = useState('');
  const [error, setError] = useState('');

  const handleDraft = async (e) => {
    e.preventDefault();
    setError('');
    try {
      const res = await axios.post('/api/emails/draft', { input });
      setDraft(res.data.draft);
    } catch (err) {
      console.error(err);
      setError('Error generating email draft.');
    }
  };

  return (
    <div className="App">
      <h1>Smart Email Assistant</h1>
      <form onSubmit={handleDraft}>
        <textarea 
          value={input} 
          onChange={(e) => setInput(e.target.value)} 
          placeholder="Enter a brief summary or key points for your email..."
          rows="5"
          cols="50"
        />
        <br />
        <button type="submit">Generate Draft</button>
      </form>
      {error && <p style={{ color: 'red' }}>{error}</p>}
      {draft && (
        <div>
          <h2>Email Draft</h2>
          <p>{draft}</p>
        </div>
      )}
    </div>
  );
}

export default App;
